// ============================================================================
// Public API Functions
// ============================================================================

/**
 * Get airline logo SVG by ICAO or IATA code.
 *
 * @param {string} code - Airline ICAO code (e.g., 'UAL', 'AAL') or callsign
 * @returns {string} SVG markup string
 *
 * @example
 * const logo = getAirlineLogo('UAL');
 * document.getElementById('logo').innerHTML = logo;
 */
function getAirlineLogo(code) {
    if (!code) return AIRLINE_LOGOS['DEFAULT'];

    const upperCode = code.toUpperCase().trim();

    // Direct ICAO match - the happy path
    if (AIRLINE_LOGOS[upperCode]) {
        return AIRLINE_LOGOS[upperCode];
    }

    // Try extracting airline code from callsign (first 3 chars)
    // e.g., "UAL1234" -> "UAL"
    if (upperCode.length >= 3) {
        const prefix = upperCode.substring(0, 3);
        if (AIRLINE_LOGOS[prefix]) {
            return AIRLINE_LOGOS[prefix];
        }
    }

    // When all else fails, return the generic airplane
    return AIRLINE_LOGOS['DEFAULT'];
}


/**
 * Get airline name from ICAO code.
 *
 * @param {string} code - Airline ICAO code
 * @returns {string|null} Full airline name or null if not found
 *
 * @example
 * getAirlineName('UAL'); // Returns "United Airlines"
 */
function getAirlineName(code) {
    const AIRLINE_NAMES = {
        'AAL': 'American Airlines',
        'DAL': 'Delta Air Lines',
        'UAL': 'United Airlines',
        'SWA': 'Southwest Airlines',
        'JBU': 'JetBlue Airways',
        'ASA': 'Alaska Airlines',
        'FFT': 'Frontier Airlines',
        'NKS': 'Spirit Airlines',
        'ACA': 'Air Canada',
        'WJA': 'WestJet',
        'WEN': 'WestJet Encore',
        'POE': 'Porter Airlines',
        'FLE': 'Flair Airlines',
        'TSC': 'Air Transat',
        'BAW': 'British Airways',
        'DLH': 'Lufthansa',
        'AFR': 'Air France',
        'KLM': 'KLM Royal Dutch Airlines',
        'UAE': 'Emirates',
        'QTR': 'Qatar Airways',
        'THY': 'Turkish Airlines',
        'QFA': 'Qantas',
        'SIA': 'Singapore Airlines',
        'ANA': 'All Nippon Airways',
        'JAL': 'Japan Airlines',
        'CPA': 'Cathay Pacific',
        'SKW': 'SkyWest Airlines',
        'RPA': 'Republic Airways',
        'JZA': 'Jazz Aviation',
        'ENY': 'Envoy Air',
        'FDX': 'FedEx Express',
        'UPS': 'UPS Airlines',
        'SWG': 'Sunwing Airlines',
    };

    if (!code) return null;

    const upperCode = code.toUpperCase().trim();

    if (AIRLINE_NAMES[upperCode]) {
        return AIRLINE_NAMES[upperCode];
    }

    // Try prefix extraction
    if (upperCode.length >= 3) {
        const prefix = upperCode.substring(0, 3);
        if (AIRLINE_NAMES[prefix]) {
            return AIRLINE_NAMES[prefix];
        }
    }

    return null;
}


/**
 * Get airline alliance by ICAO code.
 *
 * @param {string} code - Airline ICAO code
 * @returns {string|null} Alliance name or null if not in an alliance
 *
 * @example
 * getAirlineAlliance('UAL'); // Returns "Star Alliance"
 * getAirlineAlliance('NKS'); // Returns null (Spirit isn't in any alliance)
 */
function getAirlineAlliance(code) {
    if (!code) return null;

    const upperCode = code.toUpperCase().trim();

    if (AIRLINE_ALLIANCES[upperCode]) {
        return AIRLINE_ALLIANCES[upperCode];
    }

    // Try prefix
    if (upperCode.length >= 3) {
        const prefix = upperCode.substring(0, 3);
        if (AIRLINE_ALLIANCES[prefix]) {
            return AIRLINE_ALLIANCES[prefix];
        }
    }

    return null;
}


/**
 * Get alliance logo SVG.
 *
 * @param {string} alliance - Alliance name ('Star Alliance', 'oneworld', 'SkyTeam')
 * @returns {string|null} SVG markup string or null if not found
 */
function getAllianceLogo(alliance) {
    return ALLIANCE_LOGOS[alliance] || null;
}


/**
 * Get complete alliance info for an airline.
 * This is the convenience function you probably want.
 *
 * @param {string} code - Airline ICAO code
 * @returns {object|null} { name: string, logo: string } or null if not in an alliance
 *
 * @example
 * const info = getAirlineAllianceInfo('UAL');
 * if (info) {
 *     console.log(info.name);  // "Star Alliance"
 *     element.innerHTML = info.logo;  // SVG markup
 * }
 */
function getAirlineAllianceInfo(code) {
    const alliance = getAirlineAlliance(code);
    if (!alliance) return null;

    return {
        name: alliance,
        logo: ALLIANCE_LOGOS[alliance] || null,
    };
}
